export const EMBEDDED_WALLET_PATH = "/sdk/2022-08-12/embedded-wallet";
export const HEADLESS_GOOGLE_OAUTH_ROUTE = `${EMBEDDED_WALLET_PATH}/auth/headless-google-login-managed`;

export const WALLET_USER_DETAILS_LOCAL_STORAGE_NAME = (clientId: string) =>
  `paperEwsWalletUserDetails-${clientId}`;
export const WALLET_USER_ID_LOCAL_STORAGE_NAME = (clientId: string) =>
  `paperEwsWalletUserId-${clientId}`;
const AUTH_TOKEN_LOCAL_STORAGE_PREFIX = "walletToken";
export const AUTH_TOKEN_LOCAL_STORAGE_NAME = (clientId: string) => {
  return `${AUTH_TOKEN_LOCAL_STORAGE_PREFIX}-${clientId}`;
};
const DEVICE_SHARE_LOCAL_STORAGE_PREFIX = "a";
export const DEVICE_SHARE_LOCAL_STORAGE_NAME = (
  clientId: string,
  userId: string,
) => `${DEVICE_SHARE_LOCAL_STORAGE_PREFIX}-${clientId}-${userId}`;
export const DEVICE_SHARE_LOCAL_STORAGE_NAME_DEPRECATED = (clientId: string) =>
  `${DEVICE_SHARE_LOCAL_STORAGE_PREFIX}-${clientId}`;
import type {
  AuthDetails,
  InitializedUser,
  SetUpWalletRpcReturnType,
} from "./EmbeddedWallets/EmbeddedWallets";

export enum RecoveryShareManagement {
  USER_MANAGED = "USER_MANAGED",
  AWS_MANAGED = "AWS_MANAGED",
}

export type AdvancedOptions<T extends RecoveryShareManagement> = {
  // This is a hack to allow us to assign default value to recoveryShareManagement
  recoveryShareManagement: RecoveryShareManagement | T;
};

export enum AuthProvider {
  PAPER_EMAIL_OTP = "PaperEmailOTP",
  GOOGLE = "Google",
  TWITTER = "Twitter",
  COGNITO = "Cognito",
  AUTH0 = "Auth0",
  CUSTOM_JWT = "CustomJWT",
}

export type GetSocialLoginClientIdReturnType = {
  clientId: string;
};

export type GetHeadlessLoginLinkReturnType = {
  loginLink: string;
};

// TODO: Clean up tech debt of random type Objects
// E.g. StoredTokenType is really not used anywhere but it exists as this object for legacy reason
export type StoredTokenType = {
  jwtToken: string;
  authProvider: AuthProvider;
  authDetails: AuthDetails;
  developerClientId: string;
};

export type AuthStoredTokenWithCookieReturnType = {
  storedToken: StoredTokenType & {
    cookieString: string;
    shouldStoreCookieString: boolean;
    isNewUser: boolean;
  };
};
export type AuthAndWalletRpcReturnType = AuthStoredTokenWithCookieReturnType & {
  walletDetails: SetUpWalletRpcReturnType;
};

export type AuthLoginReturnType = { user: InitializedUser };
import type {
  Chain,
  CustomizationOptionsType,
} from "@paperxyz/sdk-common-utilities";
import type { EmbeddedWallet } from "../../lib/EmbeddedWallets/EmbeddedWallet";
import type { EmbeddedWalletIframeCommunicator } from "../../utils/iFrameCommunication/EmbeddedWalletIframeCommunicator";
import type {
  AdvancedOptions,
  AuthAndWalletRpcReturnType,
  RecoveryShareManagement,
} from "../Auth";

// Class constructor types
// types for class constructors still a little messy right now.
// Open to PRs from whoever sees this and knows of a cleaner way to handle things
export type ClientIdConstructorType = { clientId: string };
export type PaperConstructorType<T extends RecoveryShareManagement> =
  ClientIdConstructorType & {
    chain: Chain;
    onAuthSuccess?: (authResult: AuthAndWalletRpcReturnType) => void;
    advancedOptions?: Partial<AdvancedOptions<T>>;
    styles?: CustomizationOptionsType;
  };
export type ClientIdWithQuerierType = ClientIdConstructorType & {
  querier: EmbeddedWalletIframeCommunicator<any>;
};
export type ClientIdWithQuerierAndChainType = ClientIdWithQuerierType & {
  chain: Chain;
};

// Auth Types
export type AuthDetails = {
  email?: string;
  userWalletId: string;
  recoveryCode?: string;
};

export type InitializedUser = {
  status: UserStatus.LOGGED_IN_WALLET_INITIALIZED;
  wallet: EmbeddedWallet;
  walletAddress: string;
  authDetails: AuthDetails;
};

// Embedded Wallet Types
export enum UserStatus {
  LOGGED_OUT = "Logged Out",
  LOGGED_IN_WALLET_INITIALIZED = "Logged In, Wallet Initialized",
}
export enum UserWalletStatus {
  LOGGED_OUT = "Logged Out",
  LOGGED_IN_WALLET_UNINITIALIZED = "Logged In, Wallet Uninitialized",
  LOGGED_IN_NEW_DEVICE = "Logged In, New Device",
  LOGGED_IN_WALLET_INITIALIZED = "Logged In, Wallet Initialized",
}

export type WalletAddressObjectType = {
  walletAddress: string;
};

export type SetUpWalletRpcReturnType = WalletAddressObjectType & {
  deviceShareStored: string;
  isIframeStorageEnabled: boolean;
};

export type SendEmailOtpReturnType = {
  recoveryShareManagement: RecoveryShareManagement;
  isNewUser: boolean;
  isNewDevice: boolean;
};
export type LogoutReturnType = { success: boolean };
export type GetAuthDetailsReturnType = { authDetails?: AuthDetails };

// ! Types seem repetitive, but the name should identify which goes where
// this is the return type from the EmbeddedWallet Class getUserWalletStatus method iframe call
export type GetUserWalletStatusRpcReturnType =
  | {
      status: UserWalletStatus.LOGGED_OUT;
      user: undefined;
    }
  | {
      status: UserWalletStatus.LOGGED_IN_WALLET_UNINITIALIZED;
      user: { authDetails: AuthDetails };
    }
  | {
      status: UserWalletStatus.LOGGED_IN_NEW_DEVICE;
      user: { authDetails: AuthDetails; walletAddress: string };
    }
  | {
      status: UserWalletStatus.LOGGED_IN_WALLET_INITIALIZED;
      user: Omit<InitializedUser, "wallet" | "status">;
    };

// this is the return type from the EmbeddedWallet Class getUserWalletStatus method
export type GetUserWalletStatusFnReturnType =
  | {
      status: UserWalletStatus.LOGGED_OUT;
      user: undefined;
    }
  | {
      status: UserWalletStatus.LOGGED_IN_WALLET_UNINITIALIZED;
      user: { authDetails: AuthDetails };
    }
  | {
      status: UserWalletStatus.LOGGED_IN_NEW_DEVICE;
      user: { authDetails: AuthDetails; walletAddress: string };
    }
  | {
      status: UserWalletStatus.LOGGED_IN_WALLET_INITIALIZED;
      user: Omit<InitializedUser, "status">;
    };

// This is returned from the getUser method in PaperEmbeddedWalletSdk
export type GetUser =
  | {
      status: UserStatus.LOGGED_OUT;
    }
  | InitializedUser;
import type { CustomizationOptionsType } from "@paperxyz/sdk-common-utilities";
import { getPaperOriginUrl } from "@paperxyz/sdk-common-utilities";
import { EMBEDDED_WALLET_PATH } from "../../constants/settings";
import { LocalStorage } from "../Storage/LocalStorage";
import { IframeCommunicator } from "./IframeCommunicator";

export class EmbeddedWalletIframeCommunicator<
  T extends { [key: string]: any },
> extends IframeCommunicator<T> {
  clientId: string;
  constructor({
    clientId,
    customizationOptions,
  }: {
    clientId: string;
    customizationOptions?: CustomizationOptionsType;
  }) {
    super({
      iframeId: EMBEDDED_WALLET_IFRAME_ID,
      link: createEmbeddedWalletIframeLink({
        clientId,
        path: EMBEDDED_WALLET_PATH,
        queryParams: customizationOptions,
      }).href,
      container: document.body,
    });
    this.clientId = clientId;
  }

  override async onIframeLoadedInitVariables() {
    const localStorage = new LocalStorage({
      clientId: this.clientId,
    });

    return {
      authCookie: await localStorage.getAuthCookie(),
      deviceShareStored: await localStorage.getDeviceShare(),
      walletUserId: await localStorage.getWalletUserId(),
      clientId: this.clientId,
    };
  }
}

// This is the URL and ID tag of the iFrame that we communicate with
export function createEmbeddedWalletIframeLink({
  clientId,
  path,
  queryParams,
}: {
  clientId: string;
  path: string;
  queryParams?: { [key: string]: string | number };
}) {
  const embeddedWalletUrl = new URL(path, getPaperOriginUrl());
  if (queryParams) {
    for (const queryKey of Object.keys(queryParams)) {
      embeddedWalletUrl.searchParams.set(
        queryKey,
        queryParams[queryKey]?.toString() || "",
      );
    }
  }
  embeddedWalletUrl.searchParams.set("clientId", clientId);
  return embeddedWalletUrl;
}
export const EMBEDDED_WALLET_IFRAME_ID = "paper-embedded-wallet-iframe";
import {
  AUTH_TOKEN_LOCAL_STORAGE_NAME,
  DEVICE_SHARE_LOCAL_STORAGE_NAME,
  WALLET_USER_ID_LOCAL_STORAGE_NAME,
} from "../../constants/settings";

const data = new Map<string, string>();

export class LocalStorage {
  protected isSupported: boolean;
  protected clientId: string;
  constructor({ clientId }: { clientId: string }) {
    this.isSupported = typeof window !== "undefined" && !!window.localStorage;
    this.clientId = clientId;
  }

  protected async getItem(key: string): Promise<string | null> {
    if (this.isSupported) {
      return window.localStorage.getItem(key);
    } else {
      return data.get(key) ?? null;
    }
  }

  protected async setItem(key: string, value: string): Promise<void> {
    if (this.isSupported) {
      return window.localStorage.setItem(key, value);
    } else {
      data.set(key, value);
    }
  }

  protected async removeItem(key: string): Promise<boolean> {
    const item = await this.getItem(key);
    if (this.isSupported && item) {
      window.localStorage.removeItem(key);
      return true;
    }
    return false;
  }

  async saveAuthCookie(cookie: string): Promise<void> {
    await this.setItem(AUTH_TOKEN_LOCAL_STORAGE_NAME(this.clientId), cookie);
  }
  async getAuthCookie(): Promise<string | null> {
    return this.getItem(AUTH_TOKEN_LOCAL_STORAGE_NAME(this.clientId));
  }
  async removeAuthCookie(): Promise<boolean> {
    return this.removeItem(AUTH_TOKEN_LOCAL_STORAGE_NAME(this.clientId));
  }

  async saveDeviceShare(share: string, userId: string): Promise<void> {
    await this.saveWalletUserId(userId);
    await this.setItem(
      DEVICE_SHARE_LOCAL_STORAGE_NAME(this.clientId, userId),
      share,
    );
  }
  async getDeviceShare(): Promise<string | null> {
    const userId = await this.getWalletUserId();
    if (userId) {
      return this.getItem(
        DEVICE_SHARE_LOCAL_STORAGE_NAME(this.clientId, userId),
      );
    }
    return null;
  }
  async removeDeviceShare(): Promise<boolean> {
    const userId = await this.getWalletUserId();
    if (userId) {
      return this.removeItem(
        DEVICE_SHARE_LOCAL_STORAGE_NAME(this.clientId, userId),
      );
    }
    return false;
  }

  async getWalletUserId(): Promise<string | null> {
    return this.getItem(WALLET_USER_ID_LOCAL_STORAGE_NAME(this.clientId));
  }
  async saveWalletUserId(userId: string): Promise<void> {
    await this.setItem(
      WALLET_USER_ID_LOCAL_STORAGE_NAME(this.clientId),
      userId,
    );
  }
  async removeWalletUserId(): Promise<boolean> {
    return this.removeItem(WALLET_USER_ID_LOCAL_STORAGE_NAME(this.clientId));
  }
}
import type { MessageType, StyleObject } from "@paperxyz/sdk-common-utilities";
import { getPaperOriginUrl } from "@paperxyz/sdk-common-utilities";
import { EMBEDDED_WALLET_PATH } from "../../constants/settings";

type IFrameCommunicatorProps = {
  link: string;
  iframeId: string;
  container?: HTMLElement;
  iframeStyles?: StyleObject;
  onIframeInitialize?: () => void;
};

function sleep(seconds: number) {
  return new Promise((resolve) => {
    setTimeout(resolve, seconds * 1000);
  });
}

const iframeBaseStyle = {
  height: "100%",
  width: "100%",
  border: "none",
  backgroundColor: "transparent",
  colorScheme: "light",
  position: "fixed",
  top: "0px",
  right: "0px",
  zIndex: "2147483646",
  display: "none",
};

// Global var to help track iframe state
const isIframeLoaded = new Map<string, boolean>();

export class IframeCommunicator<T extends { [key: string]: any }> {
  private iframe: HTMLIFrameElement;
  private POLLING_INTERVAL_SECONDS = 1.4;
  private POST_LOAD_BUFFER_SECONDS = 1;

  constructor({
    link,
    iframeId,
    container = document.body,
    iframeStyles,
    onIframeInitialize,
  }: IFrameCommunicatorProps) {
    // Creating the IFrame element for communication
    let iframe = document.getElementById(iframeId) as HTMLIFrameElement | null;
    const hrefLink = new URL(link);
    const sdkVersion = process.env.SDK_VERSION;
    if (!sdkVersion) {
      throw new Error("Missing SDK_VERSION env var");
    }
    hrefLink.searchParams.set("sdkVersion", sdkVersion);
    if (!iframe || iframe.src != hrefLink.href) {
      // ! Do not update the hrefLink here or it'll cause multiple re-renders
      if (!iframe) {
        iframe = document.createElement("iframe");
        const mergedIframeStyles = {
          ...iframeBaseStyle,
          ...iframeStyles,
        };
        Object.assign(iframe.style, mergedIframeStyles);
        iframe.setAttribute("id", iframeId);
        iframe.setAttribute("fetchpriority", "high");
        container.appendChild(iframe);
      }
      iframe.src = hrefLink.href;
      iframe.setAttribute("data-version", sdkVersion);
      iframe.onload = this.onIframeLoadHandler(
        iframe,
        this.POST_LOAD_BUFFER_SECONDS,
        onIframeInitialize,
      );
    }
    this.iframe = iframe;
  }

  protected async onIframeLoadedInitVariables(): Promise<Record<string, any>> {
    return {};
  }

  onIframeLoadHandler(
    iframe: HTMLIFrameElement,
    prePostMessageSleepInSeconds: number,
    onIframeInitialize?: () => void,
  ) {
    return async () => {
      const promise = new Promise<boolean>(async (res, rej) => {
        const channel = new MessageChannel();
        channel.port1.onmessage = (event: MessageEvent<MessageType<void>>) => {
          const { data } = event;
          channel.port1.close();
          if (!data.success) {
            return rej(new Error(data.error));
          }
          isIframeLoaded.set(iframe.src, true);
          if (onIframeInitialize) {
            onIframeInitialize();
          }
          return res(true);
        };
        // iFrame takes a bit of time after loading to be ready for message receiving
        // This is hacky
        await sleep(prePostMessageSleepInSeconds);
        const INIT_IFRAME_EVENT = "initIframe";
        iframe?.contentWindow?.postMessage(
          // ? We initialise the iframe with a bunch
          // of useful information so that we don't have to pass it
          // through in each of the future call. This would be where we do it.
          {
            eventType: INIT_IFRAME_EVENT,
            data: await this.onIframeLoadedInitVariables(),
          },
          `${getPaperOriginUrl()}${EMBEDDED_WALLET_PATH}`,
          [channel.port2],
        );
      });
      await promise;
    };
  }

  async call<ReturnData>({
    procedureName,
    params,
    showIframe = false,
    injectRecoveryCode = { isInjectRecoveryCode: false },
  }: {
    procedureName: keyof T;
    params: T[keyof T];
    showIframe?: boolean;
    injectRecoveryCode?: {
      getRecoveryCode?: (userWalletId: string) => Promise<string | undefined>;
      isInjectRecoveryCode: boolean;
    };
  }) {
    while (!isIframeLoaded.get(this.iframe.src)) {
      await sleep(this.POLLING_INTERVAL_SECONDS);
    }
    if (showIframe) {
      this.iframe.style.display = "block";
      // magic number to let the display render before performing the animation of the modal in
      await sleep(0.005);
    }
    const promise = new Promise<ReturnData>((res, rej) => {
      if (injectRecoveryCode.isInjectRecoveryCode) {
        const injectRecoveryCodeListener = async (
          e: MessageEvent<{ type: string; userWalletId: string }>,
        ) => {
          if (
            e.origin !== getPaperOriginUrl() ||
            e.data.type !== "paper_getRecoveryCode" ||
            typeof e.data.userWalletId !== "string"
          ) {
            return;
          }
          const recoveryCode = await injectRecoveryCode.getRecoveryCode?.(
            e.data.userWalletId,
          );
          this.iframe.contentWindow?.postMessage(
            { type: "paper_getRecoveryCode_response", recoveryCode },
            getPaperOriginUrl(),
          );
          window.removeEventListener("message", injectRecoveryCodeListener);
        };
        window.addEventListener("message", injectRecoveryCodeListener);
      }

      const channel = new MessageChannel();
      channel.port1.onmessage = async (
        event: MessageEvent<MessageType<ReturnData>>,
      ) => {
        const { data } = event;
        channel.port1.close();
        if (showIframe) {
          // magic number to let modal fade out before hiding it
          await sleep(0.1);
          this.iframe.style.display = "none";
        }
        if (!data.success) {
          rej(new Error(data.error));
        } else {
          res(data.data);
        }
      };
      this.iframe.contentWindow?.postMessage(
        { eventType: procedureName, data: params },
        `${getPaperOriginUrl()}${EMBEDDED_WALLET_PATH}`,
        [channel.port2],
      );
    });
    return promise;
  }

  /**
   * This has to be called by any iframe that will be removed from the DOM.
   * Use to make sure that we reset the global loaded state of the particular iframe.src
   */
  destroy() {
    isIframeLoaded.delete(this.iframe.src);
  }
}
import { getPaperOriginUrl } from "@paperxyz/sdk-common-utilities";
import type {
  AuthAndWalletRpcReturnType,
  AuthLoginReturnType,
  GetHeadlessLoginLinkReturnType,
} from "../../interfaces/Auth";
import { RecoveryShareManagement } from "../../interfaces/Auth";
import { AbstractLogin } from "./AbstractLogin";

export class AwsManagedLogin extends AbstractLogin<
  void,
  { email: string },
  { email: string; otp: string }
> {
  override async loginWithPaperModal(): Promise<AuthLoginReturnType> {
    await this.preLogin();
    const result = await this.LoginQuerier.call<AuthAndWalletRpcReturnType>({
      procedureName: "loginWithPaperModal",
      params: { recoveryShareManagement: RecoveryShareManagement.AWS_MANAGED },
      showIframe: true,
      injectRecoveryCode: {
        isInjectRecoveryCode: true,
      },
    });
    return this.postLogin(result);
  }

  private async getGoogleLoginUrl(): Promise<GetHeadlessLoginLinkReturnType> {
    const result = await this.LoginQuerier.call<GetHeadlessLoginLinkReturnType>(
      {
        procedureName: "getHeadlessGoogleLoginLink",
        params: undefined,
      },
    );
    return result;
  }

  private closeWindow = ({
    isWindowOpenedByFn,
    win,
    closeOpenedWindow,
  }: {
    win?: Window | null;
    isWindowOpenedByFn: boolean;
    closeOpenedWindow?: (openedWindow: Window) => void;
  }) => {
    if (isWindowOpenedByFn) {
      win?.close();
    } else {
      if (win && closeOpenedWindow) {
        closeOpenedWindow(win);
      } else if (win) {
        win.close();
      }
    }
  };

  override async loginWithGoogle(args?: {
    openedWindow?: Window | null;
    closeOpenedWindow?: (openedWindow: Window) => void;
  }): Promise<AuthLoginReturnType> {
    await this.preLogin();
    let win = args?.openedWindow;
    let isWindowOpenedByFn = false;
    if (!win) {
      win = window.open("", "Login", "width=350, height=500");
      isWindowOpenedByFn = true;
    }
    if (!win) {
      throw new Error("Something went wrong opening pop-up");
    }
    await this.preLogin();
    // fetch the url to open the login window from iframe
    const { loginLink } = await this.getGoogleLoginUrl();

    win.location.href = loginLink;

    // listen to result from the login window
    const result = await new Promise<AuthAndWalletRpcReturnType>(
      (resolve, reject) => {
        // detect when the user closes the login window
        const pollTimer = window.setInterval(async () => {
          if (!win) {
            return;
          }
          if (win.closed) {
            clearInterval(pollTimer);
            window.removeEventListener("message", messageListener);
            reject(new Error("User closed login window"));
          }
        }, 1000);

        const messageListener = async (
          event: MessageEvent<{
            eventType: string;
            authResult?: AuthAndWalletRpcReturnType;
            error?: string;
          }>,
        ) => {
          if (event.origin !== getPaperOriginUrl()) {
            return;
          }
          if (typeof event.data !== "object") {
            reject(new Error("Invalid event data"));
            return;
          }

          switch (event.data.eventType) {
            case "userLoginSuccess": {
              window.removeEventListener("message", messageListener);
              clearInterval(pollTimer);
              this.closeWindow({
                isWindowOpenedByFn,
                win,
                closeOpenedWindow: args?.closeOpenedWindow,
              });
              if (event.data.authResult) {
                resolve(event.data.authResult);
              }
              break;
            }
            case "userLoginFailed": {
              window.removeEventListener("message", messageListener);
              clearInterval(pollTimer);
              this.closeWindow({
                isWindowOpenedByFn,
                win,
                closeOpenedWindow: args?.closeOpenedWindow,
              });
              reject(new Error(event.data.error));
              break;
            }
            case "injectDeveloperClientId": {
              win?.postMessage(
                {
                  eventType: "injectDeveloperClientIdResult",
                  developerClientId: this.clientId,
                },
                getPaperOriginUrl(),
              );
