import type {
  AdvancedOptions,
  AuthAndWalletRpcReturnType,
  AuthLoginReturnType,
  AuthProvider,
} from "../../interfaces/Auth";
import { RecoveryShareManagement } from "../../interfaces/Auth";
import type {
  ClientIdWithQuerierType,
  LogoutReturnType,
  SendEmailOtpReturnType,
} from "../../interfaces/EmbeddedWallets/EmbeddedWallets";
import { LocalStorage } from "../../utils/Storage/LocalStorage";
import type { EmbeddedWalletIframeCommunicator } from "../../utils/iFrameCommunication/EmbeddedWalletIframeCommunicator";
import { AwsManagedLogin } from "./AwsManagedLogin";
import { UserManagedLogin } from "./UserManagedLogin";

export type AuthQuerierTypes = {
  initIframe: {
    clientId: string;
    authCookie: string;
    walletUserId: string;
    deviceShareStored: string;
  };
  loginWithJwtAuthCallback: {
    token: string;
    authProvider: AuthProvider;
    recoveryCode?: string;
  };
  logout: void;
  sendPaperEmailLoginOtp: { email: string };
};

export class Auth<
  T extends RecoveryShareManagement = RecoveryShareManagement.USER_MANAGED,
> {
  protected advancedOptions: AdvancedOptions<T>;
  protected clientId: string;
  protected AuthQuerier: EmbeddedWalletIframeCommunicator<AuthQuerierTypes>;
  protected localStorage: LocalStorage;
  protected onAuthSuccess: (
    authResults: AuthAndWalletRpcReturnType,
  ) => Promise<AuthLoginReturnType>;
  public userManagedLogin: UserManagedLogin;
  public awsManagedLogin: AwsManagedLogin;

  /**
   * Used to manage the user's auth states. This should not be instantiated directly.
   * Call {@link PaperEmbeddedWalletSdk.auth} instead.
   *
   * Authentication settings can be managed via the [authentication settings dashboard](https://withpaper.com/dashboard/embedded-wallets/auth-settings)
   * @param {string} params.clientId the clientId associated with the various authentication settings
   */
  constructor({
    clientId,
    advancedOptions,
    querier,
    onAuthSuccess,
  }: ClientIdWithQuerierType & {
    advancedOptions?: Partial<AdvancedOptions<T>>;
    onAuthSuccess: (
      authDetails: AuthAndWalletRpcReturnType,
    ) => Promise<AuthLoginReturnType>;
  }) {
    this.clientId = clientId;
    this.advancedOptions = {
      recoveryShareManagement:
        advancedOptions?.recoveryShareManagement ??
        RecoveryShareManagement.AWS_MANAGED,
    };
    this.AuthQuerier = querier;
    this.localStorage = new LocalStorage({ clientId });
    this.onAuthSuccess = onAuthSuccess;
    this.userManagedLogin = new UserManagedLogin({
      postLogin: async (result) => {
        return this.postLogin(result);
      },
      preLogin: async () => {
        await this.preLogin();
      },
      querier: querier,
      clientId,
    });
    this.awsManagedLogin = new AwsManagedLogin({
      postLogin: async (result) => {
        return this.postLogin(result);
      },
      preLogin: async () => {
        await this.preLogin();
      },
      querier: querier,
      clientId,
    });
  }

  private async preLogin() {
    await this.logout();
  }

  private async postLogin({
    storedToken,
    walletDetails,
  }: AuthAndWalletRpcReturnType): Promise<AuthLoginReturnType> {
    if (storedToken.shouldStoreCookieString) {
      await this.localStorage.saveAuthCookie(storedToken.cookieString);
    }
    const initializedUser = await this.onAuthSuccess({
      storedToken,
      walletDetails,
    });
    return initializedUser;
  }

  /**
   * @description
   * Used to log the user in with an oauth login flow
   *
   * Note that you have to either enable "Auth0" or "Custom JSON Web Token" in the [auth setting dashboard](https://withpaper.com/dashboard/auth-settings) in order to use this
   * @param {string} jwtParams.token The associate token from the oauth callback
   * @param {AuthProvider} jwtParams.provider The Auth provider that is being used
   * @param {string} jwtParams.recoveryCode This has to be passed in if the user is not logging in for the first time in order for us to decrypt and recover the users wallet
   * @returns {{user: InitializedUser}} An InitializedUser object containing the user's status, wallet, authDetails, and more
   */
  async loginWithJwtAuth({
    token,
    authProvider,
    recoveryCode,
  }: AuthQuerierTypes["loginWithJwtAuthCallback"]): Promise<AuthLoginReturnType> {
    await this.preLogin();
    const result = await this.AuthQuerier.call<AuthAndWalletRpcReturnType>({
      procedureName: "loginWithJwtAuthCallback",
      params: {
        token,
        authProvider,
        recoveryCode,
      },
    });
    return this.postLogin(result);
  }

  /**
   * @description
   * Used to log the user into their Paper wallet on your platform via a myriad of auth providers
   *
   * @example
   * const Paper = new PaperEmbeddedWalletSdk({clientId: "YOUR_CLIENT_ID", chain: "Polygon"})
   * try {
   *   const user = await Paper.auth.loginWithPaperModal();
   *   // user is now logged in
   * } catch (e) {
   *   // User closed modal or something else went wrong during the authentication process
   *   console.error(e)
   * }
   *
   * @param {(userWalletId: string) => Promise<string | undefined>} args.getRecoveryCode Only present when using RecoveryShareManagement.USER_MANAGED recovery share management. A function that returns the recovery code for a given userWalletId.
   *
   * @returns {{user: InitializedUser}} An InitializedUser object. See {@link PaperEmbeddedWalletSdk.getUser} for more
   */
  async loginWithPaperModal(
    args?: T extends RecoveryShareManagement.AWS_MANAGED
      ? void
      : Parameters<UserManagedLogin["loginWithPaperModal"]>[0],
  ): Promise<AuthLoginReturnType> {
    await this.preLogin();
    if (
      this.advancedOptions.recoveryShareManagement ===
      RecoveryShareManagement.AWS_MANAGED
    ) {
      return this.awsManagedLogin.loginWithPaperModal();
    }
    return this.userManagedLogin.loginWithPaperModal(
      args as Parameters<UserManagedLogin["loginWithPaperModal"]>[0],
    );
  }

  /**
   * @description
   * Used to log the user into their Paper wallet using email OTP
   *
   * @example
   *  // Basic Flow
   *  const Paper = new PaperEmbeddedWalletSdk({clientId: "", chain: "Polygon"});
   *  try {
   *    // prompts user to enter the code they received
   *    const user = await Paper.auth.loginWithPaperEmailOtp({ email : "you@example.com" });
   *    // user is now logged in
   *  } catch (e) {
   *    // User closed the OTP modal or something else went wrong during the authentication process
   *    console.error(e)
   *  }
   *
   * @example
   *  // If you want users to never be prompted for a recovery code.
   *  const Paper = new PaperEmbeddedWalletSdk({clientId: "", chain: "Polygon"});
   *  try {
   *    const email = "you@example.com";
   *
   *    // getRecoveryCodeForUser is a function to get a recovery code based on an email
   *    // you write the function below
   *    const recoveryCode: string | undefined = await getRecoveryCodeForUser(email);
   *
   *    // prompts user to enter the code they received
   *    // Because you pass in a recovery code wherever possible, for existing users on a new device, they would not be prompted to enter the recovery code flow
   *    const user = await Paper.auth.loginWithPaperEmailOtp({ email, recoveryCode });
   *    // user is now logged in
   *    if (user.authDetails.recoveryCode) {
   *      // user has a recovery code that you can store to pass in to the function above
   *      // you write the function below
   *      await storeRecoveryCodeForUser(email, user.authDetails.recoveryCode);
   *    }
   *  } catch (e) {
   *    // User closed the OTP modal or something else went wrong during the authentication process
   *    console.error(e)
   *  }
   *
   * @param {string} props.email We will send the email an OTP that needs to be entered in order for them to be logged in.
   * @param {string} props.recoveryCode Only present when using RecoveryShareManagement.USER_MANAGED recovery share management. Specifies the recoveryCode for the given email. This will set recoveryCode as the code for the user if they are new, or user recoveryCode for the user if they are an existing user
   * @returns {{user: InitializedUser}} An InitializedUser object. See {@link PaperEmbeddedWalletSdk.getUser} for more
   */
  async loginWithPaperEmailOtp(
    args: T extends RecoveryShareManagement.AWS_MANAGED
      ? Parameters<AwsManagedLogin["loginWithPaperEmailOtp"]>[0]
      : Parameters<UserManagedLogin["loginWithPaperEmailOtp"]>[0],
  ): Promise<AuthLoginReturnType> {
    if (
      this.advancedOptions.recoveryShareManagement ===
      RecoveryShareManagement.AWS_MANAGED
    ) {
      return this.awsManagedLogin.loginWithPaperEmailOtp({
        email: args.email,
      });
    }
    return this.userManagedLogin.loginWithPaperEmailOtp(args);
  }

  async loginWithGoogle(
    args?: T extends RecoveryShareManagement.AWS_MANAGED
      ? Parameters<AwsManagedLogin["loginWithGoogle"]>[0]
      : Parameters<UserManagedLogin["loginWithGoogle"]>[0],
  ): Promise<AuthLoginReturnType> {
    if (
      this.advancedOptions.recoveryShareManagement ===
      RecoveryShareManagement.AWS_MANAGED
    ) {
      return this.awsManagedLogin.loginWithGoogle(args);
    }
    return this.userManagedLogin.loginWithGoogle();
  }

  /**
   * @description
   * A headless way to send the users at {email} an OTP code.
   * You need to then call {@link Auth.verifyPaperEmailLoginOtp} in order to complete the login process
   *
   * @example
   *  const Paper = new PaperEmbeddedWalletSdk({clientId: "", chain: "Polygon"});
   *  // sends user an OTP code
   * try {
   *    const { isNewUser } = await Paper.auth.sendPaperEmailLoginOtp({ email : "you@example.com" });
   * } catch(e) {
   *    // Error Sending user's email an OTP code
   *    console.error(e);
   * }
   *
   * // Then when your user is ready to verify their OTP
   * try {
   *    const user = await Paper.auth.verifyPaperEmailLoginOtp({ email: "you@example.com", otp: "6-DIGIT_CODE_HERE", recoveryCode: "Required if user is an existing user. i.e. !isNewUser"});
   * } catch(e) {
   *    // Error verifying the OTP code
   *    console.error(e)
   * }
   *
   * @param {string} props.email We will send the email an OTP that needs to be entered in order for them to be logged in.
   * @returns {{ success: boolean, isNewUser: boolean }} Success: indicating if the email was successfully sent (Note the email could still end up in the user's spam folder). IsNewUser indicates if the user is a new user to your platform
   */
  async sendPaperEmailLoginOtp({
    email,
  }: AuthQuerierTypes["sendPaperEmailLoginOtp"]): Promise<SendEmailOtpReturnType> {
    if (
      this.advancedOptions.recoveryShareManagement ===
      RecoveryShareManagement.AWS_MANAGED
    ) {
      return this.awsManagedLogin.sendPaperEmailLoginOtp({
        email,
        recoveryShareManagement: RecoveryShareManagement.AWS_MANAGED,
      });
    }
    return this.userManagedLogin.sendPaperEmailLoginOtp({
      email,
    });
  }

  /**
   *  @description
   * Used to verify the otp that the user receives from  Paper
   *
   * See {@link Auth.sendPaperEmailLoginOtp} for how the headless call flow looks like. Simply swap out the calls to `loginWithPaperEmailOtp` with `verifyPaperEmailLoginOtp`
   *
   * @param {string} props.email We will send the email an OTP that needs to be entered in order for them to be logged in.
   * @param {string} props.otp The code that the user received in their email
   * @param {string} props.recoveryCode The code that is first sent to the user when they sign up. Required if user is an existing user. i.e. !isNewUser from return params of {@link Auth.sendPaperEmailLoginOtp}
   * @returns {{user: InitializedUser}} An InitializedUser object containing the user's status, wallet, authDetails, and more
   */
  async verifyPaperEmailLoginOtp(
    args: T extends RecoveryShareManagement.AWS_MANAGED
      ? Parameters<AwsManagedLogin["verifyPaperEmailLoginOtp"]>[0]
      : Parameters<UserManagedLogin["verifyPaperEmailLoginOtp"]>[0],
  ) {
    if (
      this.advancedOptions.recoveryShareManagement ===
      RecoveryShareManagement.AWS_MANAGED
    ) {
      return this.awsManagedLogin.verifyPaperEmailLoginOtp(args);
    }
    return this.userManagedLogin.verifyPaperEmailLoginOtp(args);
  }

  /**
   * @description
   * Logs any existing user out of their wallet.
   * @returns {{success: boolean}} true if a user is successfully logged out. false if there's no user currently logged in.
   */
  async logout(): Promise<LogoutReturnType> {
    const { success } = await this.AuthQuerier.call<LogoutReturnType>({
      procedureName: "logout",
      params: undefined,
    });
    const isRemoveAuthCookie = await this.localStorage.removeAuthCookie();
    const isRemoveUserId = await this.localStorage.removeWalletUserId();

    return {
      success: success || isRemoveAuthCookie || isRemoveUserId,
    };
  }
}
import type { Networkish } from "@ethersproject/providers";
import { getDefaultProvider } from "@ethersproject/providers";
import type { Chain } from "@paperxyz/sdk-common-utilities";
import { ChainToPublicRpc } from "@paperxyz/sdk-common-utilities";
import type {
  ClientIdWithQuerierAndChainType,
  GetUserWalletStatusFnReturnType,
  GetUserWalletStatusRpcReturnType,
  SetUpWalletRpcReturnType,
  WalletAddressObjectType,
} from "../../interfaces/EmbeddedWallets/EmbeddedWallets";
import { UserWalletStatus } from "../../interfaces/EmbeddedWallets/EmbeddedWallets";

import type { EmbeddedWalletIframeCommunicator } from "../../utils/iFrameCommunication/EmbeddedWalletIframeCommunicator";
import { LocalStorage } from "../../utils/Storage/LocalStorage";
import { GaslessTransactionMaker } from "./GaslessTransactionMaker";
import { EthersSigner } from "./Signer";

export type WalletManagementTypes = {
  createWallet: void;
  setUpNewDevice: void;
  getUserStatus: void;
};
export type WalletManagementUiTypes = {
  createWalletUi: void;
  setUpNewDeviceUi: void;
};

export type EmbeddedWalletInternalHelperType = { showUi: boolean };

export class EmbeddedWallet {
  protected clientId: string;
  protected chain: Chain;
  protected walletManagerQuerier: EmbeddedWalletIframeCommunicator<
    WalletManagementTypes & WalletManagementUiTypes
  >;
  protected localStorage: LocalStorage;

  public gasless: GaslessTransactionMaker;

  /**
   * Not meant to be initialized directly. Call {@link .initializeUser} to get an instance
   * @param param0
   */
  constructor({ clientId, chain, querier }: ClientIdWithQuerierAndChainType) {
    this.clientId = clientId;
    this.chain = chain;
    this.walletManagerQuerier = querier;

    this.gasless = new GaslessTransactionMaker({
      chain,
      clientId,
      querier,
    });

    this.localStorage = new LocalStorage({ clientId });
  }

  /**
   * @internal
   * Used to set-up the user device in the case that they are using incognito
   * @param {string} param.deviceShareStored the value that is saved for the user's device share.
   * We save this into the localStorage on the site itself if we could not save it within the iframe's localStorage.
   * This happens in incognito mostly
   * @param {string} param.walletAddress User's wallet address
   * @param {boolean} param.isIframeStorageEnabled Tells us if we were able to store values in the localStorage in our iframe.
   * We need to store it under the dev's domain localStorage if we weren't able to store things in the iframe
   * @returns {{ walletAddress : string }} The user's wallet details
   */
  async postWalletSetUp({
    deviceShareStored,
    walletAddress,
    isIframeStorageEnabled,
    walletUserId,
  }: SetUpWalletRpcReturnType & {
    walletUserId: string;
  }): Promise<WalletAddressObjectType> {
    if (!isIframeStorageEnabled) {
      await this.localStorage.saveDeviceShare(deviceShareStored, walletUserId);
    }
    return { walletAddress };
  }

  /**
   * @internal
   * Gets the various status states of the user
   * @example
   *  const userStatus = await Paper.getUserWalletStatus();
   *  switch (userStatus.status) {
   *  case UserWalletStatus.LOGGED_OUT: {
   *    // User is logged out, call one of the auth methods on Paper.auth to authenticate the user
   *    break;
   *  }
   *  case UserWalletStatus.LOGGED_IN_WALLET_UNINITIALIZED: {
   *    // User is logged in, but does not have a wallet associated with it
   *    // you also have access to the user's details
   *    userStatus.user.authDetails;
   *    break;
   *  }
   *  case UserWalletStatus.LOGGED_IN_NEW_DEVICE: {
   *    // User is logged in and created a wallet already, but is missing the device shard
   *    // You have access to:
   *    userStatus.user.authDetails;
   *    userStatus.user.walletAddress;
   *    break;
   *  }
   *  case UserWalletStatus.LOGGED_IN_WALLET_INITIALIZED: {
   *    // user is logged in and wallet is all set up.
   *    // You have access to:
   *    userStatus.user.authDetails;
   *    userStatus.user.walletAddress;
   *    userStatus.user.wallet;
   *    break;
   *  }
   *}
   * @returns {GetUserWalletStatusFnReturnType} an object to containing various information on the user statuses
   */
  async getUserWalletStatus(): Promise<GetUserWalletStatusFnReturnType> {
    const userStatus =
      await this.walletManagerQuerier.call<GetUserWalletStatusRpcReturnType>({
        procedureName: "getUserStatus",
        params: undefined,
      });
    if (userStatus.status === UserWalletStatus.LOGGED_IN_WALLET_INITIALIZED) {
      return {
        status: UserWalletStatus.LOGGED_IN_WALLET_INITIALIZED,
        user: { ...userStatus.user, wallet: this },
      };
    }
    return userStatus;
  }

  /**
   * @description
   * Switches the chain that the user wallet is currently on.
   * @example
   * // user wallet will be set to Polygon
   * const Paper = new PaperEmbeddedWalletSdk({clientId: "", chain: "Polygon"});
   * const user = await Paper.initializeUser();
   * // Switch the user wallet to Mumbai
   * await user.wallet.setChain({ chain: "Mumbai" });
   * @param {Chain} params.chain The chain that we are changing the user wallet too
   */
  async setChain({ chain }: { chain: Chain }): Promise<void> {
    this.chain = chain;
    this.gasless = new GaslessTransactionMaker({
      chain,
      clientId: this.clientId,
      querier: this.walletManagerQuerier,
    });
  }

  /**
   * Returns an Ethers.Js compatible signer that you can use in conjunction with the rest of dApp
   * @example
   * const Paper = new PaperEmbeddedWalletSdk({clientId: "", chain: "Polygon"});
   * const user = await Paper.getUser();
