import type { Chain } from "@paperxyz/sdk-common-utilities";
import type { ClientIdWithQuerierAndChainType } from "../../interfaces/EmbeddedWallets/EmbeddedWallets";
import type {
  CallContractReturnType,
  ContractCallInputType,
} from "../../interfaces/EmbeddedWallets/GaslessTransactionMaker";
import type { EmbeddedWalletIframeCommunicator } from "../../utils/iFrameCommunication/EmbeddedWalletIframeCommunicator";

export type GaslessTransactionQuerierTypes = {
  callContract: {
    contractAddress: string;
    method: {
      stub: string;
      args: Array<unknown>;
    };
    chain: Chain;
  };
};

/**
 * @description GaslessTransactionMaker is used to execute gasless transactions from the embedded wallets
 */
export class GaslessTransactionMaker {
  protected chain: Chain;
  protected clientId: string;
  protected gaslessTransactionQuerier: EmbeddedWalletIframeCommunicator<GaslessTransactionQuerierTypes>;
  constructor({ chain, clientId, querier }: ClientIdWithQuerierAndChainType) {
    this.chain = chain;
    this.clientId = clientId;
    this.gaslessTransactionQuerier = querier;
  }
  /**
   * @description
   * Use to call arbitrary contracts on the blockchain. Note that you have to enable {@link https://withpaper.com/dashboard/developers Sponsored Fees} in order for this to work.
   *
   * @param {string} params.contractAddress The address for which the contract call is directed too.
   * @param {string} params.methodInterface the function stub on the contract. This looks something like `function myFunctionName(address user, uint256 tokenId) external payable`. Refer to this {@link https://blog.ricmoo.com/human-readable-contract-abis-in-ethers-js-141902f4d917 ethers.js article} for more.
   * @param {Array} params.methodArgs The arguments that is to be passed to the contract in order that they are to be passed to the contract.
   * @throws if there is an error calling the contract for whatever reason.
   * @returns {{ transactionHash: string }} The transaction hash associated with the successful contract call.
   */
  async callContract({
    contractAddress,
    methodArgs,
    methodInterface,
  }: ContractCallInputType): Promise<CallContractReturnType> {
    return await this.gaslessTransactionQuerier.call<CallContractReturnType>({
      procedureName: "callContract",
      params: {
        chain: this.chain,
        contractAddress,
        method: {
          args: methodArgs,
          stub: methodInterface,
        },
      },
    });
  }
}
import type {
  Provider,
  TransactionRequest,
} from "@ethersproject/abstract-provider";
import type {
  TypedDataDomain,
  TypedDataField,
} from "@ethersproject/abstract-signer";
import { Signer } from "@ethersproject/abstract-signer";
import type { Bytes } from "@ethersproject/bytes";
import type { Deferrable } from "@ethersproject/properties";
import { defineReadOnly } from "@ethersproject/properties";
import type { ClientIdWithQuerierType } from "../../interfaces/EmbeddedWallets/EmbeddedWallets";
import type {
  GetAddressReturnType,
  SignMessageReturnType,
  SignTransactionReturnType,
  SignedTypedDataReturnType,
} from "../../interfaces/EmbeddedWallets/Signer";

import type { JsonRpcProvider } from "@ethersproject/providers";
import type { EmbeddedWalletIframeCommunicator } from "../../utils/iFrameCommunication/EmbeddedWalletIframeCommunicator";

export type SignerProcedureTypes = {
  getAddress: void;
  signMessage: {
    message: string | Bytes;
    chainId: number;
    rpcEndpoint: string;
  };
  signTransaction: {
    transaction: Deferrable<TransactionRequest>;
    chainId: number;
    rpcEndpoint: string;
  };
  signTypedDataV4: {
    domain: TypedDataDomain;
    types: Record<string, Array<TypedDataField>>;
    message: Record<string, unknown>;
    chainId: number;
    rpcEndpoint: string;
  };
  connect: { provider: Provider };
};

export class EthersSigner extends Signer {
  protected querier: EmbeddedWalletIframeCommunicator<SignerProcedureTypes>;
  protected clientId: string;
  protected endpoint: string;
  private DEFAULT_ETHEREUM_CHAIN_ID = 5;
  constructor({
    provider,
    clientId,
    querier,
  }: ClientIdWithQuerierType & {
    provider: Provider;
  }) {
    super();
    this.clientId = clientId;
    this.querier = querier;
    // we try to extract a url if possible
    this.endpoint = (provider as JsonRpcProvider).connection?.url;
    defineReadOnly(this, "provider", provider);
  }

  override async getAddress(): Promise<string> {
    const { address } = await this.querier.call<GetAddressReturnType>({
      procedureName: "getAddress",
      params: undefined,
    });
    return address;
  }

  override async signMessage(message: string | Bytes): Promise<string> {
    const network = await this.provider?.getNetwork();
    if (network) {
      network._defaultProvider;
    }

    const { signedMessage } = await this.querier.call<SignMessageReturnType>({
      procedureName: "signMessage",
      params: {
        message,
        chainId:
          (await this.provider?.getNetwork())?.chainId ??
          this.DEFAULT_ETHEREUM_CHAIN_ID,
        rpcEndpoint: this.endpoint,
      },
    });
    return signedMessage;
  }

  override async signTransaction(
    transaction: TransactionRequest,
  ): Promise<string> {
    const { signedTransaction } =
      await this.querier.call<SignTransactionReturnType>({
        procedureName: "signTransaction",
        params: {
          transaction,
          chainId:
            (await this.provider?.getNetwork())?.chainId ??
            this.DEFAULT_ETHEREUM_CHAIN_ID,
          rpcEndpoint: this.endpoint,
        },
      });
    return signedTransaction;
  }

  async _signTypedData(
    domain: SignerProcedureTypes["signTypedDataV4"]["domain"],
    types: SignerProcedureTypes["signTypedDataV4"]["types"],
    message: SignerProcedureTypes["signTypedDataV4"]["message"],
  ): Promise<string> {
    const { signedTypedData } =
      await this.querier.call<SignedTypedDataReturnType>({
        procedureName: "signTypedDataV4",
        params: {
          domain,
          types,
          message,
          chainId:
            (await this.provider?.getNetwork())?.chainId ??
            this.DEFAULT_ETHEREUM_CHAIN_ID,
          rpcEndpoint: this.endpoint,
        },
      });
    return signedTypedData;
  }

  override connect(provider: Provider): EthersSigner {
    return new EthersSigner({
      clientId: this.clientId,
      provider,
      querier: this.querier,
    });
  }
}
import { RecoveryShareManagement } from "../interfaces/Auth";
import type {
  GetUser,
  PaperConstructorType,
} from "../interfaces/EmbeddedWallets/EmbeddedWallets";
import {
  UserStatus,
  UserWalletStatus,
} from "../interfaces/EmbeddedWallets/EmbeddedWallets";
import { EmbeddedWalletIframeCommunicator } from "../utils/iFrameCommunication/EmbeddedWalletIframeCommunicator";
import type { AuthQuerierTypes } from "./Auth";
import { Auth } from "./Auth";
import { EmbeddedWallet } from "./EmbeddedWallets/EmbeddedWallet";

export class PaperEmbeddedWalletSdk<
  T extends RecoveryShareManagement = RecoveryShareManagement.USER_MANAGED,
> {
  protected clientId: string;
  protected querier: EmbeddedWalletIframeCommunicator<AuthQuerierTypes>;

  private wallet: EmbeddedWallet;
  /**
   * Used to manage the Auth state of the user.
   */
  auth: Auth<T>;

  /**
   * @example
   * const Paper = new PaperEmbeddedWalletSdk({ clientId: "", chain: "Goerli" });
   * @param {string} initParams.clientId the clientId found on the {@link https://withpaper.com/dashboard/developers developer's dashboard}
   * @param {Chain} initParams.chain sets the default chain that the EmbeddedWallet will live on.
   * @param {CustomizationOptionsType} initParams.styles sets the default style override for any modal that pops up asking for user's details when creating wallet or logging in.
   */
  constructor({
    clientId,
    chain,
    styles,
    advancedOptions,
    onAuthSuccess,
  }: PaperConstructorType<T>) {
    this.clientId = clientId;
    this.querier = new EmbeddedWalletIframeCommunicator({
      clientId,
      customizationOptions: styles,
    });
    this.wallet = new EmbeddedWallet({
      clientId,
      chain,
      querier: this.querier,
    });

    this.auth = new Auth<T>({
      clientId,
      advancedOptions: {
        recoveryShareManagement: RecoveryShareManagement.USER_MANAGED,
        ...(advancedOptions ?? {}),
      },
      querier: this.querier,
      onAuthSuccess: async (authResult) => {
        await this.wallet.postWalletSetUp({
          ...authResult.walletDetails,
          walletUserId: authResult.storedToken.authDetails.userWalletId,
        });
        await this.querier.call({
          procedureName: "initIframe",
          params: {
            deviceShareStored: authResult.walletDetails.deviceShareStored,
            clientId: this.clientId,
            walletUserId: authResult.storedToken.authDetails.userWalletId,
            authCookie: authResult.storedToken.cookieString,
          },
        });
        onAuthSuccess?.(authResult);
        return {
          user: {
            status: UserStatus.LOGGED_IN_WALLET_INITIALIZED,
            authDetails: authResult.storedToken.authDetails,
            wallet: this.wallet,
            walletAddress: authResult.walletDetails.walletAddress,
          },
        };
      },
    });
  }

  /**
   * Gets the usr if they are logged in
   * @example
   *  const user = await Paper.getUser();
   *  switch (user.status) {
   *     case UserStatus.LOGGED_OUT: {
   *       // User is logged out, call one of the auth methods on Paper.auth to authenticate the user
   *       break;
   *     }
   *     case UserStatus.LOGGED_IN_WALLET_INITIALIZED: {
   *       // user is logged in and wallet is all set up.
   *       // You have access to:
   *       user.status;
   *       user.authDetails;
   *       user.walletAddress;
   *       user.wallet;
   *       break;
   *     }
   *}
   * @returns {GetUser} an object to containing various information on the user statuses
   */
  async getUser(): Promise<GetUser> {
    const userStatus = await this.wallet.getUserWalletStatus();
    switch (userStatus.status) {
      // user gets {UserWalletStatus.LOGGED_IN_NEW_DEVICE} when they log in but never complete the recovery flow and exits (close modal, refresh etc)
      case UserWalletStatus.LOGGED_IN_NEW_DEVICE:
      // User gets {UserWalletStatus.LOGGED_IN_WALLET_UNINITIALIZED} when they log in but manage to exit the client in the small window between auth completion and sending them their wallet recovery details
      case UserWalletStatus.LOGGED_IN_WALLET_UNINITIALIZED:
        // in both case, we simply log them out to reset their state
        await this.auth.logout();
        return this.getUser();
      case UserWalletStatus.LOGGED_OUT:
        return {
          status: UserStatus.LOGGED_OUT,
        };
      case UserWalletStatus.LOGGED_IN_WALLET_INITIALIZED:
        return {
          status: UserStatus.LOGGED_IN_WALLET_INITIALIZED,
          ...userStatus.user,
        };
    }
  }
}
